import React, { useState, useRef, useEffect } from 'react';
import './style.css';

import Item from "./Item";
import styled from 'styled-components';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const Cards = () => {

  const breakPoints = [
    { width: 1, itemsToShow: 3 },
    { width: 550, itemsToShow: 3 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 3 },
  ];

  const items = ['One', 'Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight'];

  const duplicatedItems = [...items, ...items];

  const Cont = styled.div`
    width : 1700px;
`;


  const Pagination = ({ pages, activePage, onClick }: { pages: number; activePage: number; onClick: (index: number) => void }) => {
    return (
      <div>
        {Array.from(Array(pages), (_, index) => (
          <button
            key={index}
            onClick={() => onClick(index * 3)} // Adjust the slide count as per your requirement
            style={{
              margin: '0 5px',
              backgroundColor: activePage === index * 3 ? '#00008B' : '#fff',
              color: activePage === index * 3 ? '#fff' : '#00008B',
            }}
          >
            {index * 3 + 1} - {index * 3 + 3} {/* Adjust the slide count as per your requirement */}
          </button>
        ))}
      </div>
    );
  };

    
  return (
    <>
       <h1 style={{ textAlign: "center" }}>Example to setup your carousel in react</h1>
       <div className="App">

      <Cont>
      <Carousel breakPoints={breakPoints} isRTL={false} enableAutoPlay={true} autoPlaySpeed={5000} enableSwipe={true} pagination={false} 
        renderPagination={(pages: any, activePage: any, onClick: any) => (
          <Pagination pages={pages} activePage={activePage} onClick={onClick} />
        )}
      >
          {duplicatedItems.map((item, index) => (
            <Item key={index}>{item}</Item>
          ))}
        </Carousel>
      </Cont>

      </div>
    </>
  )
}

export default Cards
