import React, { useState, useRef, useEffect } from 'react';
import './style.css'

const Cards = () => {



    const Cards = () => {
        const [isDragging, setIsDragging] = useState(false);
        const [isAutoPlay, setIsAutoPlay] = useState(true);
        const carouselRef = useRef<HTMLUListElement>(null);
        const cardRef = useRef<HTMLLIElement>(null);
        const [cardPerView, setCardPerView] = useState(0);
        let startX: number = 0;
        let startScrollLeft: number = 0;
      
        useEffect(() => {
          const carouselElement = carouselRef.current;
          const cardElement = cardRef.current;
          const wrapper = document.querySelector(".wrapper") as HTMLElement;
      
          if (carouselElement && cardElement) {
            const firstCardWidth = cardElement.offsetWidth;
            const arrowBtns = document.querySelectorAll(".wrapper i");
            const carouselChildrens = Array.from(carouselElement.children) as HTMLElement[];
      
            let timeoutId: ReturnType<typeof setTimeout> | undefined;
      
            setCardPerView(Math.round(carouselElement.offsetWidth / firstCardWidth));
      
            carouselChildrens.slice(-cardPerView).reverse().forEach(card => {
              carouselElement.insertAdjacentHTML("afterbegin", card.outerHTML);
            });
      
            carouselChildrens.slice(0, cardPerView).forEach(card => {
              carouselElement.insertAdjacentHTML("beforeend", card.outerHTML);
            });
      
            carouselElement.classList.add("no-transition");
            carouselElement.scrollLeft = carouselElement.offsetWidth;
            carouselElement.classList.remove("no-transition");
      
            arrowBtns.forEach(btn => {
              btn.addEventListener("click", () => {
                carouselElement.scrollLeft += btn.id === "left" ? -firstCardWidth : firstCardWidth;
              });
            });
      
            const dragStart = (e: MouseEvent) => {
              setIsDragging(true);
              carouselElement.classList.add("dragging");
              startX = e.pageX;
              startScrollLeft = carouselElement.scrollLeft;
            };
      
            const dragging = (e: MouseEvent) => {
              if (!isDragging) return;
              carouselElement.scrollLeft = startScrollLeft - (e.pageX - startX);
            };
      
            const dragStop = () => {
              setIsDragging(false);
              carouselElement.classList.remove("dragging");
            };
      
            const infiniteScroll = () => {
              if (carouselElement.scrollLeft === 0) {
                carouselElement.classList.add("no-transition");
                carouselElement.scrollLeft = carouselElement.scrollWidth - (2 * carouselElement.offsetWidth);
                carouselElement.classList.remove("no-transition");
              } else if (Math.ceil(carouselElement.scrollLeft) === carouselElement.scrollWidth - carouselElement.offsetWidth) {
                carouselElement.classList.add("no-transition");
                carouselElement.scrollLeft = carouselElement.offsetWidth;
                carouselElement.classList.remove("no-transition");
              }
              clearTimeout(timeoutId);
              if (!wrapper.matches(":hover")) autoPlay();
            };
      
            const autoPlay = () => {
              if (window.innerWidth < 800 || !isAutoPlay) return;
              timeoutId = setTimeout(() => (carouselElement.scrollLeft += firstCardWidth), 2500);
            };
      
            autoPlay();
      
            carouselElement.addEventListener("mousedown", dragStart);
            carouselElement.addEventListener("mousemove", dragging);
            document.addEventListener("mouseup", dragStop);
            carouselElement.addEventListener("scroll", infiniteScroll);
            wrapper.addEventListener("mouseenter", () => clearTimeout(timeoutId));
            wrapper.addEventListener("mouseleave", autoPlay);
          }
        }, []);

    }
  return (
    <div>

<div className="wrapper">
      <i id="left" className="fa-solid fa-angle-left">⬅️</i>
      <ul className="carousel">
        <li className="card">
          <div className="img"><img src="images/img-1.jpg"  draggable="false"/></div>
          <h2>Blanche Pearson</h2>
          <span>Sales Manager</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-2.jpg"  draggable="false"/></div>
          <h2>Joenas Brauers</h2>
          <span>Web Developer</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-3.jpg"  draggable="false"/></div>
          <h2>Lariach French</h2>
          <span>Online Teacher</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-4.jpg"  draggable="false"/></div>
          <h2>James Khosravi</h2>
          <span>Freelancer</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-5.jpg"  draggable="false"/></div>
          <h2>Kristina Zasiadko</h2>
          <span>Bank Manager</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-6.jpg"  draggable="false"/></div>
          <h2>Donald Horton</h2>
          <span>App Designer</span>
        </li>
      </ul>
      <i id="right" className="fa-solid fa-angle-right">➡️</i>
    </div>
      
    </div>
  )
}

export default Cards
