import React, { useEffect } from "react";

import './style.css';
import Cards from "./Cards";
import Welcome from "./Welcome";
import Contact from "./Contact";
import About from "./About";

import { Link } from "react-scroll";


const MyComponent = () => {


  const handleClickScroll = () => {
    const car = document.getElementById('cards-sec');
    const cont = document.getElementById('contact-sec');
    const abou = document.getElementById('about-sec');
    if (car) {
      // ðŸ‘‡ Will scroll smoothly to the top of the next section
      car.scrollIntoView({ behavior: 'smooth' });
    }
    if (cont) {
      // ðŸ‘‡ Will scroll smoothly to the top of the next section
      cont.scrollIntoView({ behavior: 'smooth' });
    }
    if (abou) {
      // ðŸ‘‡ Will scroll smoothly to the top of the next section
      abou.scrollIntoView({ behavior: 'smooth' });
    }
  };




  const handleClickScroll2 = () => {

    const cont = document.getElementById('contact-sec');

    if (cont) {
      // ðŸ‘‡ Will scroll smoothly to the top of the next section
      cont.scrollIntoView({ behavior: 'smooth' });
    }

  };



  const handleClickScroll3 = () => {

    const abou = document.getElementById('about-sec');

    if (abou) {
      // ðŸ‘‡ Will scroll smoothly to the top of the next section
      abou.scrollIntoView({ behavior: 'smooth' });
    }
  };



  return (
    <>
        <>
          <nav>
            <div className='logo'><h2>Company name</h2></div>

            <div className='item-cont'>
              <a href="">Home</a>
              <a href="" className="btn-scroll" onClick={handleClickScroll}>Services</a>
              <a href="" onClick={handleClickScroll2}>Contact us</a>
              <a href="" onClick={handleClickScroll3}>About us</a>
            </div>
          </nav>
          <Welcome/>
          <div id='cards-sec'><Cards/></div>
          <div id="contact-sec"></div><Contact/>
          <div id="about-sec"></div><About/>
        </>
    </>







  );
};

export default MyComponent;
