import React, {useRef} from 'react';
import './style.css';
import Welcome from './Welcome';
import Cards from './Cards';
import Contact from './Contact';

const Navbar = () => {

    const homeRef = useRef<HTMLElement | null>(null);
    const servicesRef = useRef<HTMLElement | null>(null);
    const contactRef = useRef<HTMLElement | null>(null);
    const aboutRef = useRef<HTMLElement | null>(null);
  
    const scrollToSection = (ref: React.MutableRefObject<HTMLElement>) => {
      if(ref.current){
        ref.current!.scrollIntoView({
            behavior: 'smooth',
          });
      }
    };
  
    return (
      <>
        <nav>
          <div className='logo'>
            <h2>Company name</h2>
          </div>
  
          <div className='item-cont'>
            <a href="#" onClick={() => scrollToSection(homeRef)}>Home</a>
            <a href="#" onClick={() => scrollToSection(servicesRef)}>Services</a>
            <a href="#" onClick={() => scrollToSection(contactRef)}>Contact us</a>
            <a href="#" onClick={() => scrollToSection(aboutRef)}>About us</a>
          </div>
        </nav>
  
        <div className="content">
          <section ref={homeRef}>
            {<Welcome/>}
          </section>
          
          <section ref={servicesRef}>
            {<Cards/>}
          </section>
          
          <section ref={contactRef}>
            {<Contact/>}
          </section>
          
          <section ref={aboutRef}>
            {<Contact/>}
          </section>
        </div>
      </>
    );
  };

export default Navbar
