import React, { useState, useRef, useEffect } from 'react';
import './style.css'

const Cards = () => {

    const Carousel = () => {

        const wrapper = document.querySelector(".wrapper") as HTMLElement;
        const carousel = document.querySelector(".carousel") as HTMLElement;
        if (carousel) {
          const firstCardWidth = (carousel.querySelector(".card") as HTMLElement).offsetWidth;
          const arrowBtns = document.querySelectorAll(".wrapper i");
          const carouselChildrens = Array.from(carousel.children) as HTMLElement[];

          let isDragging = false, isAutoPlay = true, startX: number, startScrollLeft: number, timeoutId: string | number | NodeJS.Timeout | undefined;

          let cardPerView = Math.round((carousel as HTMLElement).offsetWidth / firstCardWidth);

          carouselChildrens.slice(-cardPerView).reverse().forEach(card => {
            carousel.insertAdjacentHTML("afterbegin", card.outerHTML);
          });

          carouselChildrens.slice(0, cardPerView).forEach(card => {
            carousel.insertAdjacentHTML("beforeend", card.outerHTML);
        });

        carousel.classList.add("no-transition");
        carousel.scrollLeft = (carousel as HTMLElement).offsetWidth;
        carousel.classList.remove("no-transition");


        arrowBtns.forEach(btn => {
            btn.addEventListener("click", () => {
                carousel.scrollLeft += btn.id == "left" ? -firstCardWidth : firstCardWidth;
            });
        });

        const dragStart = (e: { pageX: any; }) => {
            isDragging = true;
            carousel.classList.add("dragging");
            // Records the initial cursor and scroll position of the carousel
            startX = e.pageX;
            startScrollLeft = carousel.scrollLeft;
        }

        const dragging = (e: { pageX: number; }) => {
            if(!isDragging) return; // if isDragging is false return from here
            // Updates the scroll position of the carousel based on the cursor movement
            carousel.scrollLeft = startScrollLeft - (e.pageX - startX);
        }
        const dragStop = () => {
            isDragging = false;
            carousel.classList.remove("dragging");
        }

        const infiniteScroll = () => {
            // If the carousel is at the beginning, scroll to the end
            if(carousel.scrollLeft === 0) {
                carousel.classList.add("no-transition");
                carousel.scrollLeft = carousel.scrollWidth - (2 * (carousel as HTMLElement).offsetWidth);
                carousel.classList.remove("no-transition");
            }
            // If the carousel is at the end, scroll to the beginning
            else if(Math.ceil(carousel.scrollLeft) === carousel.scrollWidth - (carousel as HTMLElement).offsetWidth) {
                carousel.classList.add("no-transition");
                carousel.scrollLeft = (carousel as HTMLElement).offsetWidth;
                carousel.classList.remove("no-transition");
            }
            // Clear existing timeout & start autoplay if mouse is not hovering over carousel
            clearTimeout(timeoutId);
            if(!wrapper.matches(":hover")) autoPlay();
        }

        const autoPlay = () => {
            if(window.innerWidth < 800 || !isAutoPlay) return; // Return if window is smaller than 800 or isAutoPlay is false
            // Autoplay the carousel after every 2500 ms
            timeoutId = setTimeout(() => carousel.scrollLeft += firstCardWidth, 2500);
        }

        autoPlay();

        carousel.addEventListener("mousedown", dragStart);
        carousel.addEventListener("mousemove", dragging);
        document.addEventListener("mouseup", dragStop);
        carousel.addEventListener("scroll", infiniteScroll);
        wrapper.addEventListener("mouseenter", () => clearTimeout(timeoutId));
        wrapper.addEventListener("mouseleave", autoPlay);

        
          
        }
        



    }

  return (
    <div>

<div className="wrapper">
      <i id="left" className="fa-solid fa-angle-left">Left</i>
      <ul className="carousel">
        <li className="card">
          <div className="img"><img src="images/img-1.jpg" alt="img" draggable="false"/></div>
          <h2>Blanche Pearson</h2>
          <span>Sales Manager</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-2.jpg" alt="img" draggable="false"/></div>
          <h2>Joenas Brauers</h2>
          <span>Web Developer</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-3.jpg" alt="img" draggable="false"/></div>
          <h2>Lariach French</h2>
          <span>Online Teacher</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-4.jpg" alt="img" draggable="false"/></div>
          <h2>James Khosravi</h2>
          <span>Freelancer</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-5.jpg" alt="img" draggable="false"/></div>
          <h2>Kristina Zasiadko</h2>
          <span>Bank Manager</span>
        </li>
        <li className="card">
          <div className="img"><img src="images/img-6.jpg" alt="img" draggable="false"/></div>
          <h2>Donald Horton</h2>
          <span>App Designer</span>
        </li>
      </ul>
      <i id="right" className="fa-solid fa-angle-right"></i>
    </div>
      
    </div>
  )
}

export default Cards
