import React, {useRef} from 'react';
import './style.css';

const Navbar = () => {
    const homeRef = useRef(null);
    const servicesRef = useRef(null);
    const contactRef = useRef(null);
    const aboutRef = useRef(null);
  
    const scrollToSection = (ref: React.MutableRefObject<HTMLElement>) => {
      if(ref.current){
        ref.current.scrollIntoView({
            behavior: 'smooth',
          });
      }
    };
  
    return (
      <>
        <nav>
          <div className='logo'>
            <h2>Company name</h2>
          </div>
  
          <div className='item-cont'>
            <a href="#" onClick={() => scrollToSection(homeRef)}>Home</a>
            <a href="#" onClick={() => scrollToSection(servicesRef)}>Services</a>
            <a href="#" onClick={() => scrollToSection(contactRef)}>Contact us</a>
            <a href="#" onClick={() => scrollToSection(aboutRef)}>About us</a>
          </div>
        </nav>
  
        <div className="content">
          <section ref={homeRef}>
            {/* Home Component */}
          </section>
          
          <section ref={servicesRef}>
            {/* Services Component */}
          </section>
          
          <section ref={contactRef}>
            {/* Contact Component */}
          </section>
          
          <section ref={aboutRef}>
            {/* About Component */}
          </section>
        </div>
      </>
    );
  };

export default Navbar
