import React, { useState, useRef, useEffect } from 'react';
import './style.css';
import Carousel from 'react-elastic-carousel';
import Item from "./Item";
import styled from 'styled-components';


const Cards = () => {

  const breakPoints = [
    { width: 1, itemsToShow: 3 },
    { width: 550, itemsToShow: 3 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 3 },
  ];

  const items = ['One', 'Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight'];

  const duplicatedItems = [...items, ...items, ...items,...items,...items,...items,];

  const Cont = styled.div`
    width : 1700px;
`;

const itemsToScrollRef = useRef(3);

const [value, setValue] = useState(3);

const handleNextStart = () => {
  setValue(value === 3 ? 1 : 3);
};

useEffect(() => {
  // Update the itemsToScroll prop whenever the value changes
  // This will trigger a re-render of the component
  // The effect runs after the value state is updated
  itemsToScrollRef.current = value;
}, [value]);

const Marg = styled.div`
margin-top : 300px;
`;






    
  return (
    <>
       <Marg>
      <hr></hr>
      <Cont>
      <Carousel breakPoints={breakPoints} isRTL={false} onNextStart={handleNextStart} enableMouseSwipe={true}   itemsToScroll={value}  enableAutoPlay={true} autoPlaySpeed={2000} easing="ease" transitionMs={1000}  enableSwipe={true} pagination={false} preventDefaultTouchmoveEvent={true}>
          {duplicatedItems.map((item, index) => (
            <Item key={index}>{item}</Item>
          ))}
          
        </Carousel>
      </Cont>

      </Marg>
    </>
  )
}

export default Cards
