import React, { useState, useRef, useEffect } from 'react';
import './style.css';
import Carousel from 'react-elastic-carousel';
import Item from "./Item";
import styled from 'styled-components';


const Cards = () => {

  const breakPoints = [
    { width: 1, itemsToShow: 3 },
    { width: 550, itemsToShow: 3 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 3 },
  ];

  const items = ['One', 'Two', 'Three', 'Four', 'Five', 'Six', 'Seven', 'Eight'];

  const duplicatedItems = [...items, ...items, ...items,...items,...items,...items,];

  const Cont = styled.div`
    width : 1700px;
`;


const [value, setValue] = useState(true);


const handleNextStart = () => {
  setValue(!value);
};






    
  return (
    <>
       <h1 style={{ textAlign: "center" }}>Example to setup your carousel in react</h1>
       <div className="App">

      <Cont>
      <Carousel breakPoints={breakPoints} isRTL={false} onNextStart={handleNextStart} enableMouseSwipe={true} itemsToScroll={value==true ? 3 : 1} enableAutoPlay={true} autoPlaySpeed={5000} easing="ease" transitionMs={1000}  enableSwipe={true} pagination={false} preventDefaultTouchmoveEvent={true}>
          {duplicatedItems.map((item, index) => (
            <Item key={index}>{item}</Item>
          ))}
          
        </Carousel>
      </Cont>

      </div>
    </>
  )
}

export default Cards
